using System.Threading;

namespace PageSpeedAPI
{
    class Program
    {
        const string _apiUri = "https://pagespeedonline.googleapis.com/pagespeedonline/v5/runPagespeed";
        const string _outputFileName = "pageSpeedInfo.txt";
        static string _outputFolderPath = $@"Output\{DateTime.Now.ToString("yyyyMMdd")}";
        static string _directory = Path.GetDirectoryName(Assembly.GetExecutingAssembly().Location);
        static string _configPath = $@"{_directory}\_config\";

        static void Main(string[] args)
        {
            //Directory.GetCurrentDirectory();
            List<RequestDTO> requestList = GetConfigData<List<RequestDTO>>("requestList.json");
            List<ResponseMappingDTO> mapList = GetConfigData<List<ResponseMappingDTO>>("mapList.json");

            string jsonResponse = string.Empty;
            ResponseDTO responseObj = null;

            foreach (var req in requestList)
            {
                jsonResponse = GetRequest(req);
                responseObj = JsonConvert.DeserializeObject<ResponseDTO>(jsonResponse);

                OutputDTO output = new OutputDTO();
                InitializeOuputInfo(output, req.siteName, req.subSiteName, req.strategy);

                MappingResponseToOutput(responseObj, output, mapList);
                WriteJsonToFile(output, Path.Combine(_directory, _outputFolderPath), _outputFileName);
            }
            Console.WriteLine("完成操作");
        }

        static T GetConfigData<T>(string confFileName)
        {
            string jsonString = string.Empty;
            try
            {
                StreamReader sr = new StreamReader($"{_configPath}{confFileName}");
                jsonString = sr.ReadToEnd();
                sr.Close();
            }
            catch (Exception e) {
                Console.WriteLine("Exception: " + e.Message);
            }
            return JsonConvert.DeserializeObject<T>(jsonString);
        }

        static string GetRequest(RequestDTO rInfo)
        {
            var isError = false;
            string jsonResponse = string.Empty;
       
            do
            {
                try {
                    string requestUrl = $@"{_apiUri}?url={System.Web.HttpUtility.UrlEncode(rInfo.url)}&strategy={rInfo.strategy}&local=zh_TW";
                    Console.WriteLine($"Send Request: {rInfo.siteName,-6}_{rInfo.strategy,-7}_{rInfo.subSiteName,-10} {rInfo.url,-50}");
                    WebRequest request = WebRequest.Create(requestUrl);
                    WebResponse response = request.GetResponse();

                    using (Stream dataStream = response.GetResponseStream())
                    {
                        StreamReader reader = new StreamReader(dataStream);
                        jsonResponse = reader.ReadToEnd();
                    }

                    response.Close();
                    isError = false;
                } catch (WebException ex) {
                    Console.WriteLine($"HTTP Error: {ex.Status}, after 10 seconds retry...");
                    Thread.Sleep(10000);
                    isError = true;
                }
            } while (isError);

            return jsonResponse;

