        static void MappingResponseToOutput(ResponseDTO src, OutputDTO target, List<ResponseMappingDTO> mapList)
        {
            foreach(var info in mapList)
            {
                var value = GetPropertyValue(src, info.InputProperty);
                SetProperty(target, info.OutputProperty, value);
            }
        }

        public static object GetPropertyValue(object src, string propertyName)
        {
            if (propertyName.Contains(".")) {
                var temp = propertyName.Split(new char[] { '.' }, 2);
                return GetPropertyValue(GetPropertyValue(src, temp[0]), temp[1]);
            } else {
                var prop = src.GetType().GetProperty(propertyName);
                return prop?.GetValue(src, null);
            }
        }

        static void SetProperty(object target, string propertyName, object value)
        {
            string[] propertyNames = propertyName.Split('.');
            for (int i = 0; i < propertyNames.Length - 1; i++)
            {
                PropertyInfo property = target.GetType().GetProperty(propertyNames[i]);
                var propertyValue = property.GetValue(target, null);
                if (propertyValue is null && property.PropertyType.IsClass)
                {
                    propertyValue = Activator.CreateInstance(property.PropertyType);
                    property.SetValue(target, propertyValue);
                }
                target = propertyValue;
            }
            target.GetType().GetProperty(propertyNames.Last()).SetValue(target, value);
        }

        static void InitializeOuputInfo(OutputDTO output, string siteName, string subSiteName, string strategy)
        {
            output.site = siteName;
            output.name = subSiteName;
            output.device = strategy;
            output.date = DateTime.Now.ToString("yyyy/MM/dd HH:mm:ss");
        }

        static  void WriteJsonToFile(OutputDTO src, string folder, string fileName)
        {
            if (!Directory.Exists(folder)) {
                DirectoryInfo di = Directory.CreateDirectory(folder);
            } 

            string filePath = $@"{folder}\{fileName}";
            string jsonString = string.Concat(JsonConvert.SerializeObject(src), Environment.NewLine);
            // 指定路徑檔案不存在
            if (!File.Exists(filePath)) {
                File.WriteAllText(filePath, jsonString);
            } else {
                using (StreamWriter file = new StreamWriter(filePath, append: true)) {
                    file.Write(jsonString);
                }
            }
        }
